<?phpnamespace App\Http\Controllers;use Illuminate\Http\Request;use App\Bairro;use App\Http\Requests;use Flash;class BairroController extends Controller{    public function __construct() {        $this->middleware('auth');    }    public function index() {        $bairros = Bairro::all();        return view('sistema.bairro.index', ['bairros' => $bairros]);    }        public function store(Request $request) {        $bairro = Bairro::create($request->all());        Flash::success('Bairro Adicionado com sucesso!');        return $request->json($bairro);    }    public function show($id, Request $request) {        if($request->ajax())        {            $bairro = Bairro::find($id);            return $bairro;        }else{            return 'Requisição HTTP';        }    }    public function update($id, Request $request) {        if($request->ajax())        {            $bairro = Bairro::find($id);            $bairro->descricao = $request->descricao;            $bairro->save();            Flash::success('Bairro atualizado com sucesso!');            return $request->json($bairro);        }else{            return 'Requisição HTTP';        }    }}